mapper.select:
SELECT ino, isummary, im_img_path as imImgPath FROM interior WHERE itype='portfolio'
and istyle in
#{istyleItem}
and all_color in
#{colorItem}
and dtype = #{field}
order by ino desc
order by ino desc
order by ihits desc
order by ino asc

SELECT ino, isummary, im_img_path as imImgPath FROM interior WHERE itype='model'
and istyle in
#{istyleItem}
and all_color in
#{colorItem}
and dtype = #{field}
order by ino desc
order by ino desc
order by ihits desc
order by ino asc
